mapper.select:
	/* UserMapper.xml - doList */

	SELECT
         email
        ,password
        ,create_date
		FROM 	USERS
		WHERE   1=1
		AND     email = #{email}
		AND     password = #{password}

/* UserMapper.xml - doIdCheck */

SELECT COUNT(id) AS cnt 
	FROM 	USERS
	WHERE   1=1
	AND     email = #{email}
